define('taoTests/test/runner/mocks/mockPlugin', ['taoTests/runner/plugin'], function (pluginFactory) {
    'use strict';

    return pluginFactory({
        name: 'mock',
        init: function () {
            var self = this;
            setTimeout(function() {
                self.trigger('loaded');
            }, 250);
        }
    });
});
define('taoTests/test/runner/mocks/mockRunnerProvider', [], function () {
    'use strict';

    return {
        name: 'mock-runner',
        init() {
            setTimeout( () => this.trigger('mock-runner-loaded'), 250);
        },
        loadAreaBroker() {

        }
    };
});
define('taoTests/test/runner/mocks/bundledMockRunnerProvider', [], function () {
    'use strict';

    return {
        name: 'mock-runner-from-bundle',
        init() {},
        loadAreaBroker() {}
    };
});
